import React, { Component } from 'react';
import MultiseriesChart from './views/Multiseries Chart';
import TheRest from './views/TheRest';
import Stock from "./views/userInputFunction/userInputFunction";


let contStyles = {
    backgroundColor: 'red',
    width: '70vw',
    margin: '0 auto'
}

let searchQuerry;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTearm: ""
    }

    this.handleTermChange=this.handleTermChange.bind(this);
    this.search=this.search.bind(this);
  }

  handleTermChange(event){
    searchQuerry = Stock.search(event.target.value);              
  }

  search(event){
    event.preventDefault();    
    this.setState({searchTearm: searchQuerry});            
    return this.state
  }

  render() {    
    return (
      <div className="mainDIv">
        <div className="container" style={contStyles}>
          <MultiseriesChart appState={this.state.searchTearm}/>
          <form>
            <input placeholder="Search by index" onChange={this.handleTermChange} /> 
            <button onClick={this.search} >Search</button>
          </form>
        </div>
        <TheRest />
      </div>
    );
  }
}

export default App;
