import React, { Component } from 'react';
import './styles.css';
import CanvasJSReact from '../assets/canvasjs.react';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
 
// let thestyles = {
// 	backgroundColor: 'red',
// 	width: '90vw',

// }

let options;

class MultiseriesChart extends Component {	
	constructor(props) {
		super(props);
		this.state = {
		  stockChartXValues: [],
		  stockChartYValues: []
		//   where do we write info from the api
		}						
	  }
	
	  componentDidMount() {
		this.fetchStock()		
	  }
	//   reading api on start
	
	  fetchStock() {
		const pointerToThis = this;
		// console.log(pointerToThis);
		const API_KEY = '740WRQPFQK10PTAG';
		let StockSymbol = 'INX';
		let API_Call = `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY_ADJUSTED&symbol=${StockSymbol}&outputsize=compact&apikey=${API_KEY}`;
		let stockChartXValuesFunction = [];
		let stockChartYValuesFunction = [];
	
		fetch(API_Call)
		  .then(
			function(response) {
			  return response.json();
			}
		  ) /* actually fetching data */
		  .then(
			function(data) {	
			  for (var key in data['Time Series (Daily)']) {
				stockChartXValuesFunction.push(key);
				stockChartYValuesFunction.push(Math.floor(Number(data['Time Series (Daily)'][key]['1. open'])));
			}
			// pushing them in the state
			
			  pointerToThis.setState({
				stockChartXValues: stockChartXValuesFunction,
				stockChartYValues: stockChartYValuesFunction
				//  /\state.............../\value from the function above
			  })
			//   console.log(pointerToThis.state);
			  
			}
		  )
	  }
	// iterates over the function above and pushes values one by one to a new var and returns it
	Stock1(param) {
		let y = [];
		for (let i = 0; i < this.state.stockChartXValues.length; i++) {
			y.push({y: this.state.stockChartYValues[i], label: this.state.stockChartXValues[i]})
		}
		return y
	}
	
	componentDidUpdate(){
		console.log(this.props.appState);
	}
	
	options = {
		animationEnabled: true,	
		zoomEnabled: true,
		includeZero: false,
		title:{
			text: "Stock Perfomance"
		},
		axisY2 : {
			title: "Price(USD)",

		},
		toolTip: {
			shared: true
		},
		
		data: [
			{
			  type: "line",
			  name: "date",
			  showInLegend: true,

			  dataPoints: this.Stock1()
			},
		{
			type: "line",
			name: "2017",
			showInLegend: true,
			axisYType: "secondary",
			dataPoints: 
			[
				{ y: 172, label: "Jan" },
				{ y: 173, label: "Feb" },
				{ y: 175, label: "Mar" },
				{ y: 172, label: "Apr" },
				{ y: 162, label: "May" },
				{ y: 165, label: "Jun" },
				{ y: 172, label: "Jul" },
				{ y: 168, label: "Aug" },
				{ y: 175, label: "Sept" },
				{ y: 170, label: "Oct" },
				{ y: 165, label: "Nov" },
				{ y: 169, label: "Dec" }
			]
		},
		{
			type: "line",
			name: "2017",
			showInLegend: true,
			axisYType: "secondary",
			dataPoints: null
}
		]}

	render() {
		
		
		
		return (
		<div /* style={thestyles} */>
			<h1>React Multiseries Chart</h1>
			<CanvasJSChart options = {options} 
				/* onRef={ref => this.chart = ref} */
			/>
			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
		</div>
		);
	}
}

export default MultiseriesChart;
